registros.registro:
/*SETOR*/ SELECT DISTINCT X.cd_setor_atendimento2, X.ds_setor_atendimento, X.IE_EVOLUCAO_CLINICA
FROM ( /*SEM ALTA*/ SELECT b.cd_setor_atendimento cd_setor_atendimento2, b.ds_setor_atendimento, a.IE_EVOLUCAO_CLINICA FROM atendimento_paciente_v b, evolucao_paciente a WHERE a.dt_evolucao BETWEEN :dt_inicial AND fim_dia(:dt_final) AND a.nr_atendimento = b.nr_atendimento AND (( b.cd_setor_atendimento =:cd_setor_atendimento )OR ( 0 =:cd_setor_atendimento )) AND b.dt_alta IS NULL AND a.dt_liberacao IS NOT NULL AND a.ie_situacao = 'A' AND :ie_alta = 'S' UNION /*COM ALTA*/ SELECT b.cd_setor_atendimento cd_setor_atendimento2, b.ds_setor_atendimento, a.IE_EVOLUCAO_CLINICA FROM atendimento_paciente_v b, evolucao_paciente a WHERE a.dt_evolucao BETWEEN :dt_inicial AND fim_dia(:dt_final) AND a.nr_atendimento = b.nr_atendimento AND (( b.cd_setor_atendimento =:cd_setor_atendimento )OR ( 0 =:cd_setor_atendimento )) AND b.dt_alta IS NOT NULL AND a.dt_liberacao IS NOT NULL AND a.ie_situacao = 'A' AND :ie_alta = 'C' UNION /*AMBOS*/ SELECT b.cd_setor_atendimento cd_setor_atendimento2, b.ds_setor_atendimento, a.IE_EVOLUCAO_CLINICA FROM atendimento_paciente_v b, evolucao_paciente a WHERE a.dt_evolucao BETWEEN :dt_inicial AND fim_dia(:dt_final) AND a.nr_atendimento = b.nr_atendimento AND (( b.cd_setor_atendimento =:cd_setor_atendimento )OR ( 0 =:cd_setor_atendimento )) AND a.dt_liberacao IS NOT NULL AND a.ie_situacao = 'A' AND :ie_alta = 'A' )X WHERE 1 = 1 AND :IE_TIPO_REL = 'A' @SQL_WHERE ORDER BY 2 >
> Setor >

/*PACIENTE*/ SELECT DISTINCT X.nm_paciente, X.cd_setor_atendimento3, X.IE_EVOLUCAO_CLINICA, x.nr_atendimento2, x.cd_pessoa_fisica
FROM ( /*SEM ALTA*/ SELECT DISTINCT b.nm_paciente, a.ie_evolucao_clinica, b.cd_setor_atendimento cd_setor_atendimento3, b.nr_atendimento nr_atendimento2, a.cd_pessoa_fisica cd_pessoa_fisica FROM atendimento_paciente_v b, evolucao_paciente a WHERE a.dt_evolucao BETWEEN :dt_inicial AND fim_dia(:dt_final) AND a.nr_atendimento = b.nr_atendimento AND (( b.cd_setor_atendimento =:cd_setor_atendimento2)OR ( 0 =:cd_setor_atendimento2)) AND b.dt_alta IS NULL AND a.dt_liberacao IS NOT NULL AND a.ie_situacao = 'A' AND :ie_alta = 'S' UNION /*COM ALTA*/ SELECT DISTINCT b.nm_paciente, a.ie_evolucao_clinica, b.cd_setor_atendimento cd_setor_atendimento3, b.nr_atendimento nr_atendimento2, a.cd_pessoa_fisica cd_pessoa_fisica FROM atendimento_paciente_v b, evolucao_paciente a WHERE a.dt_evolucao BETWEEN :dt_inicial AND fim_dia(:dt_final) AND a.nr_atendimento = b.nr_atendimento AND (( b.cd_setor_atendimento =:cd_setor_atendimento2)OR ( 0 =:cd_setor_atendimento2)) AND b.dt_alta IS NOT NULL AND a.dt_liberacao IS NOT NULL AND a.ie_situacao = 'A' AND :ie_alta = 'C' UNION /*AMBOS*/ SELECT DISTINCT b.nm_paciente, a.ie_evolucao_clinica, b.cd_setor_atendimento cd_setor_atendimento3, b.nr_atendimento nr_atendimento2, a.cd_pessoa_fisica cd_pessoa_fisica FROM atendimento_paciente_v b, evolucao_paciente a WHERE a.dt_evolucao BETWEEN :dt_inicial AND fim_dia(:dt_final) AND a.nr_atendimento = b.nr_atendimento AND (( b.cd_setor_atendimento =:cd_setor_atendimento2)OR ( 0 =:cd_setor_atendimento2)) AND a.dt_liberacao IS NOT NULL AND a.ie_situacao = 'A' AND :ie_alta = 'A' )X WHERE 1 = 1 AND :IE_TIPO_REL = 'A' @SQL_WHERE ORDER BY 1 >
> Paciente >

/*SETOR TOTALIZADOR*/ SELECT DISTINCT T.cd_setor_atendimento2, T.ds_setor_atendimento FROM ( SELECT DISTINCT x.cd_setor_atendimento2, x.ds_setor_atendimento, x.IE_EVOLUCAO_CLINICA FROM ( /*SEM ALTA*/ SELECT DISTINCT b.cd_setor_atendimento cd_setor_atendimento2, b.ds_setor_atendimento, A.IE_EVOLUCAO_CLINICA FROM atendimento_paciente_v b, evolucao_paciente a WHERE a.dt_evolucao BETWEEN :dt_inicial AND fim_dia(:dt_final) AND a.nr_atendimento = b.nr_atendimento AND (( b.cd_setor_atendimento =:cd_setor_atendimento)OR ( 0 =:cd_setor_atendimento)) AND b.dt_alta IS NULL AND a.dt_liberacao IS NOT NULL AND a.ie_situacao = 'A' AND :ie_alta = 'S' UNION /*COM ALTA*/ SELECT DISTINCT b.cd_setor_atendimento cd_setor_atendimento2, b.ds_setor_atendimento, A.IE_EVOLUCAO_CLINICA FROM atendimento_paciente_v b, evolucao_paciente a WHERE a.dt_evolucao BETWEEN :dt_inicial AND fim_dia(:dt_final) AND a.nr_atendimento = b.nr_atendimento AND (( b.cd_setor_atendimento =:cd_setor_atendimento)OR ( 0 =:cd_setor_atendimento)) AND b.dt_alta IS NOT NULL AND a.dt_liberacao IS NOT NULL AND a.ie_situacao = 'A' AND :ie_alta = 'C' UNION /*AMBOS*/ SELECT DISTINCT b.cd_setor_atendimento cd_setor_atendimento2, b.ds_setor_atendimento, A.IE_EVOLUCAO_CLINICA FROM atendimento_paciente_v b, evolucao_paciente a WHERE a.dt_evolucao BETWEEN :dt_inicial AND fim_dia(:dt_final) AND a.nr_atendimento = b.nr_atendimento AND (( b.cd_setor_atendimento =:cd_setor_atendimento)OR ( 0 =:cd_setor_atendimento)) AND a.dt_liberacao IS NOT NULL AND a.ie_situacao = 'A' AND :ie_alta = 'A' )X WHERE 1 = 1 @SQL_WHERE and :IE_TIPO_REL = 'S'
)T Order by 2 >
> Setor Totalizador >

/*TOTALIZADOR*/ SELECT count(x.CD_EVOLUCAO) total, x.IE_EVOLUCAO_CLINICA, decode(x.DS_SUBTIPO_EVOLUCAO,null,'Sala de Recuperação',x.DS_SUBTIPO_EVOLUCAO)DS_SUBTIPO_EVOLUCAO, x.cd_setor_atendimento
FROM ( /*SEM ALTA*/ SELECT a.CD_EVOLUCAO, A.IE_EVOLUCAO_CLINICA, SUBSTR(OBTER_DESC_TIPO_EVOLUCAO(a.IE_EVOLUCAO_CLINICA),1,200) DS_SUBTIPO_EVOLUCAO, b.cd_setor_atendimento FROM atendimento_paciente_v b, evolucao_paciente a WHERE a.dt_evolucao BETWEEN :dt_inicial AND fim_dia(:dt_final) AND a.nr_atendimento = b.nr_atendimento AND (( b.cd_setor_atendimento =:cd_setor_atendimento2)OR ( 0 =:cd_setor_atendimento2)) AND b.dt_alta IS NULL AND a.dt_liberacao IS NOT NULL AND a.ie_situacao = 'A' AND :ie_alta = 'S' UNION /*COM ALTA*/ SELECT a.CD_EVOLUCAO, A.IE_EVOLUCAO_CLINICA, SUBSTR(OBTER_DESC_TIPO_EVOLUCAO(a.IE_EVOLUCAO_CLINICA),1,200) DS_SUBTIPO_EVOLUCAO, b.cd_setor_atendimento FROM atendimento_paciente_v b, evolucao_paciente a WHERE a.dt_evolucao BETWEEN :dt_inicial AND fim_dia(:dt_final) AND a.nr_atendimento = b.nr_atendimento AND (( b.cd_setor_atendimento =:cd_setor_atendimento2)OR ( 0 =:cd_setor_atendimento2)) AND b.dt_alta IS NOT NULL AND a.dt_liberacao IS NOT NULL AND a.ie_situacao = 'A' AND :ie_alta = 'C' UNION /*AMBOS*/ SELECT a.CD_EVOLUCAO, A.IE_EVOLUCAO_CLINICA, SUBSTR(OBTER_DESC_TIPO_EVOLUCAO(a.IE_EVOLUCAO_CLINICA),1,200) DS_SUBTIPO_EVOLUCAO, b.cd_setor_atendimento FROM atendimento_paciente_v b, evolucao_paciente a WHERE a.dt_evolucao BETWEEN :dt_inicial AND fim_dia(:dt_final) AND a.nr_atendimento = b.nr_atendimento AND (( b.cd_setor_atendimento =:cd_setor_atendimento2)OR ( 0 =:cd_setor_atendimento2)) AND a.dt_liberacao IS NOT NULL AND a.ie_situacao = 'A' AND :ie_alta = 'A' )X WHERE 1 = 1 @SQL_WHERE AND :IE_TIPO_REL = 'S' group by x.IE_EVOLUCAO_CLINICA, decode(x.DS_SUBTIPO_EVOLUCAO,null,'Sala de Recuperação',x.DS_SUBTIPO_EVOLUCAO), x.cd_setor_atendimento ORDER BY DS_SUBTIPO_EVOLUCAO >
> Totalizador >


